int GetTotalByZip(orders d, int zip) {
  ArrayList items = new ArrayList();
  foreach(order o in d){ if(o.zip == zip) items.Add(o.item); }
  int total = 0;
  foreach (item x in items) total += x.price*x.quantity;
  return total;
}

