// FromTo(n,m) ==> n, n+1, ...,m
int* FromTo(int n, int m){
  for(int i=n; i=<m; i++) yield i;
}

public class bibliography {
  book* books;
}

string* getTitles(books bs){
  foreach(book it in bs) yield it.title;
}

